import React from "react";
import { useState } from "react";
import { getStays } from "../api";
import { Link } from "react-router-dom";

export default function Stays() {
  const [stays, setStays] = useState([]);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);
  React.useEffect(() => {
    async function loadStays() {
      setLoading(true);
      try {
        const data = await getStays();
        setStays(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }

    loadStays();
  }, []);
  const stayElements = stays.map((item) => (
    <div key={item.id}>
      <Link to={item.id}>
        <img src={item.imgSrc} className="stays-images" />
        <h2>{item.name}</h2>
        <div
          className={
            item.type === "City"
              ? "city-type"
              : item.type === "Beach"
              ? "beach-type"
              : item.type === "Mountain"
              ? "mountain-type"
              : ""
          }
        >
          <p>{item.type}</p>
        </div>
      </Link>
    </div>
  ));
  return (
    <div>
      <div className="stays-list-filter">
        <div className="stays-buttons-filter">
          <button>city</button>
          <button>beach</button>
          <button>mountain</button>
        </div>
      </div>
      <div className="stays-list-container">{stayElements}</div>
    </div>
  );
}
